CREATE table timeline ( _id INTEGER NOT NULL
      PRIMARY KEY,created_at INTEGER  NOT NULL , status TEXT NOT NULL, userCode INTEGER NOT NULL,
       isDisaster INTEGER, isFavorite INTEGER);

CREATE table DisasterTable ( _id INTEGER NOT NULL PRIMARY KEY
     , created_at INTEGER NOT NULL, added_at INTEGER, status TEXT NOT NULL, userCode INTEGER NOT NULL, sent_by TEXT,
      isFromServer INTEGER NOT NULL, hasBeenSent INTEGER, isValid INTEGER NOT NULL, hopCount INTEGER, signature BLOB);

CREATE table DirectMessagesTable ( _id INTEGER NOT NULL
    	PRIMARY KEY,created_at INTEGER NOT NULL, status TEXT NOT NULL, user TEXT, userCode INTEGER,
    	 isDisaster INTEGER);

CREATE table SearchTable ( _id INTEGER NOT NULL
       PRIMARY KEY,created_at INTEGER NOT NULL, status TEXT NOT NULL, user TEXT NOT NULL,
       userCode INTEGER NOT NULL, isDisaster INTEGER);

CREATE table FriendsTable ( _id INTEGER NOT NULL
   		PRIMARY KEY, user TEXT NOT NULL, modulus TEXT, exponent TEXT,
   		 isDisasterFriend INTEGER NOT NULL, isFollower INTEGER, isFollowed INTEGER);

CREATE table DirectOutgoingTable ( _id INTEGER NOT NULL
    	PRIMARY KEY,created_at INTEGER NOT NULL, status BLOB NOT NULL, recipientUser TEXT NOT NULL, user BLOB NOT NULL,
    	 isDisaster INTEGER, hasBeenSent INTEGER);
